mapper.insert:
INSERT INTO vam_author (
    authorName,
    nationId,
    authorIntro
)
VALUE (
    #{authorName},
    #{nationId},
    #{authorIntro}
)

mapper.select:
SELECT authorid, authorname, nationid, regdate, updatedate FROM vam_author
WHERE authorname LIKE concat('%', #{keyword}, '%')
ORDER BY authorid DESC LIMIT #{skip}, #{amount}
